from flask import Flaskfrom pymongo import MongoClientfrom flask.ext.cors import CORSimport jsonfrom DemoClass import DemoClassfrom CommonCrime import CommonCrimefrom TimeCrime import TimeCrimefrom ArrestCrime import ArrestCrimefrom PrecentageCrime import PrecentageCrimefrom LocationCrime import LocationCrimeapp = Flask(__name__)CORS(app)client = MongoClient()client = MongoClient('localhost', 27017)test = DemoClass()cCrime = CommonCrime()tCrime = TimeCrime()aCrime = ArrestCrime()pCrime = PrecentageCrime()lCrime = LocationCrime()@app.route("/getData/number-of-tables", methods=['GET'])def demo():    return test.main_Method()@app.route("/commonCrime/year/<int:year_id>", methods=['GET'])def commonCrime(year_id):    return cCrime.main_Method()@app.route("/timeCrime/year/<int:year_id>/time/<int:time_id>", methods=['GET'])def timeCrime(year_id, time_id):    return tCrime.main_Method()@app.route("/arrestCrime/type/<crimeType>", methods=['GET'])def arrestCrime(crimeType):    return aCrime.main_Method()@app.route("/precentageCrime/year/<int:year_id>", methods=['GET'])def precentageCrime(year_id):    return pCrime.main_Method()@app.route("/locationCrime/year/<int:year_id>", methods=['GET'])def locationCrime(year_id):    return lCrime.main_Method()if __name__ == "__main__":    app.run(host='0.0.0.0')