from flask import Flaskfrom flask.ext.cors import CORSfrom pymongo import MongoClientfrom NumberCrime import NumberCrimefrom DemoClass import DemoClassfrom CommonCrime import CommonCrimefrom TimeCrime import TimeCrimefrom ArrestCrime import ArrestCrimefrom PrecentageCrime import PrecentageCrimefrom LocationCrime import LocationCrime# This method is necessary to run the REST-Service on our Amazon AWS instanceapp = Flask(__name__)CORS(app)nCrime = NumberCrime()cCrime = CommonCrime()tCrime = TimeCrime()aCrime = ArrestCrime()pCrime = PrecentageCrime()lCrime = LocationCrime()@app.route("/numberCrime", methods=['GET'])def numberCrime():    return nCrime.main_Method()@app.route("/commonCrime", methods=['GET'])def commonCrime():    return cCrime.main_Method()@app.route("/timeCrime", methods=['GET'])def timeCrime():    return tCrime.main_Method()@app.route("/arrestCrime", methods=['GET'])def arrestCrime():    return aCrime.main_Method()@app.route("/precentageCrime/year/<int:year_id>", methods=['GET'])def precentageCrime(year_id):    return pCrime.main_Method()@app.route("/locationCrime/year/<int:year_id>", methods=['GET'])def locationCrime(year_id):    return lCrime.main_Method(year_id)if __name__ == "__main__":    app.run(host='0.0.0.0')