from flask import Flaskfrom flask.ext.cors import CORSfrom pymongo import MongoClientfrom DemoClass import DemoClassfrom CommonCrime import CommonCrimefrom TimeCrime import TimeCrimefrom ArrestCrime import ArrestCrimefrom PrecentageCrime import PrecentageCrimefrom LocationCrime import LocationCrimeapp = Flask(__name__)CORS(app)cCrime = CommonCrime()tCrime = TimeCrime()aCrime = ArrestCrime()pCrime = PrecentageCrime()lCrime = LocationCrime()@app.route("/commonCrime", methods=['GET'])def commonCrime():    return cCrime.main_Method()@app.route("/timeCrime/year/<int:year_id>", methods=['GET'])def timeCrime(year_id):    return tCrime.main_Method(year_id)@app.route("/arrestCrime/type/<crimeType>", methods=['GET'])def arrestCrime(crimeType):    return aCrime.main_Method()@app.route("/precentageCrime/year/<int:year_id>", methods=['GET'])def precentageCrime(year_id):    return pCrime.main_Method()@app.route("/locationCrime/year/<int:year_id>", methods=['GET'])def locationCrime(year_id):    return lCrime.main_Method()if __name__ == "__main__":    app.run(host='0.0.0.0')